import thumbnailDesktop  from "./images/image-product-desktop.jpg"
import thumbnailMobile  from "./images/image-product-mobile.jpg"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons'
import React from "react"

function useWindowSize() {

  const [windowSize, setWindowSize] = React.useState({
    width: undefined,
    height: undefined,
  });
  React.useEffect(() => {
    function handleResize() {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }
    window.addEventListener("resize", handleResize);
    handleResize();
    return () => window.removeEventListener("resize", handleResize);
  }, []); 
  return windowSize;
}
function App() {
  const [icon, setIcon] = React.useState(thumbnailMobile)
  const windowSize = useWindowSize();
  React.useEffect(()=>{

      if(windowSize.width <= 375) {
        setIcon(thumbnailMobile)
      }
      else{
        setIcon(thumbnailDesktop)
      }
    }, [windowSize])

  return (
    <div className="App">
      <div className="container">
        <img src={icon} alt="thumbnail" className="thumbnail"></img>
        <div className="product-details">
          <p className="product-type">PERFUME</p>
          <p className="product-name">Gabreille Essence Eau De Parfum</p>
          <p className="product-desc">A floral solar and voluptuous interpretation composed by Olivier Polge, Perfume-Creator for the House of CHANEL.</p>
          <div className="product-price">
            <span className="product-selling">$149.99</span>
            <span className="product-mrp">$169.99</span>    
          </div>
          <button className="addToCart"> <FontAwesomeIcon icon={faShoppingCart} /><span className="addText">Add to cart</span></button>
        </div>
      </div>
    </div>
  );
}

export default App;
